<?php  require_once('../modules/AppModule.php');  require_once('../modules/AuthModule.php');    require_once('../libs/Templater.php');  $renderParams = array();    require_once('../models/SiteModel.php');    if ($_POST) {    require_once('../models/SiteModel.php');    $url = getPostParam('url');    $columns = array('url' => $url);    if (!SiteModel\validate($columns)) {      $renderParams['error'] = SiteModel\modelErrorToText($modelError);    } else {      SiteModel\insert($columns);      redirect('main.php');    }  }      tplRender('createSite', $renderParams);