<?php  require_once('../modules/AppModule.php');  require_once('../modules/AuthModule.php');    require_once('../libs/Templater.php');  $renderParams = array();    if ($_POST) {    require_once('../libs/Auth.php');    $login = getPostParam('login');    $password = getPostParam('password');    if (!login($login , $password)) {      $renderParams['error'] = authErrorToText($authError);    } else {      redirect('main.php');    }  }    tplRender('login', $renderParams);