<?php  require_once('../modules/AppModule.php');  require_once('../modules/AuthModule.php');    require_once('../models/SiteModel.php');  $result = array();    $lookDepth = getPostParam('depth');  if (empty($lookDepth)) {    $result['status'] = 'Значение не должно быть пустым';    echo (json_encode($result));    die();  }    if (!filter_var($lookDepth, FILTER_VALIDATE_INT)) {    $result['status'] = 'Значение должно быть целым числом';    echo (json_encode($result));    die();  }    $siteId = getPostParam('siteId');  $site = null;  if ($siteId) {    $site = \SiteModel\select(array('conditions' => 'id = :id', 'params' => array(':id' => $siteId)));  }    //if it's no such site  if (!$site) {    throw new Exception('no such site');    die();  }  $site = $site[0];      $queryResult = \SiteModel\update(    array('look_depth' => $lookDepth),    'id = :id',    array(':id' => $site['id'])  );    if ($queryResult) {    $result['status'] = 'Данные сохранены';  } else {    $result['status'] = 'Данные не изменены';  }    echo (json_encode($result));    