<?php  require_once('../modules/AppModule.php');  require_once('../modules/AuthModule.php');    require_once('../libs/Templater.php');  $renderParams = array();    require_once('../models/SiteModel.php');    $lastId = getPostParam('lastId');  if (!$lastId) {    throw new Exception('no last id');  }  $renderParams['sites'] = SiteModel\select(array(    'limit' => 15,     'order' => 'id DESC',    'conditions' => 'id < :lastId',    'params' => array(':lastId' => $lastId),  ));    if (sizeof($renderParams['sites']) > 0) {    ob_start();      tplRenderPartial('sites/sitesList', $renderParams);    $html = ob_get_contents();    ob_end_clean();    echo json_encode(array('html' => $html));  } else {    echo json_encode(array('stop' => true));  }